CustomField.formula:
IF(OR(TEXT(Classe_di_rating__c) = '1',TEXT(Classe_di_rating__c) = '2'),'Rishio molto basso',
		  IF(OR(TEXT(Classe_di_rating__c) = '3',TEXT(Classe_di_rating__c) = '4'),'Rischio basso',
			IF(OR(TEXT(Classe_di_rating__c) = '5',TEXT(Classe_di_rating__c) = '6',TEXT(Classe_di_rating__c) = '7'),'Rischio medio',
				IF(OR(TEXT(Classe_di_rating__c) = '8',TEXT(Classe_di_rating__c) = '9'),'Rischio rilevante',
					IF(TEXT(Classe_di_rating__c) >= '10','Rischio elevato','N/A'
					)
				)
			)
		  )
	)
